import*as cloud360Types from"./cloud360-types.js";export class RenderView{constructor(question,selector,parent){this.title=question.title,this.question=question.question,this.selector=selector,this.parent=parent,this.record=[]}addAccordionCard(data,expand=!1){const Content=`<div class="card">\n            <div class="card-header"\n              id="${data.internalid}-${data.question}">\n    <h2 class="mb-0">\n        <button class="btn btn-link" type="button"\n        data-toggle="collapse" data-target="#collapseQuestion${data.question}"\n        aria-expanded="${expand}"\n        aria-controls="collapseQuestion${data.question}">\n        ${data.question} | ${data.title}\n          </button>\n      </h2>\n          </div>\n\n              <div id="collapseQuestion${data.question}"\n              class="collapse ${expand?"show":""}"\n              aria-labelledby="question${data.question}" data-parent="${this.parent}">\n                  <div id="body-${data.question}" class="card-body">\n                  </div>\n              </div>\n          </div>`;$(this.parent).append(Content)}addRecord(value){this.record.push(value)}render(employees,question){switch(question.question){case"Q1":this.renderQ1(employees);break;case"Q2":this.renderPerformanceQ(employees);break;case"Q3P1":this.renderQ3(employees);break;case"Q3P2":this.renderQ3(employees,!0);break;case"Q4":this.renderQ4(employees);break;case"Q5P1":this.renderQ5(employees);break;case"Q5P2":this.renderPerformanceQ(employees,!0);break;default:$(`#pre-${this.question}`).html(JSON.stringify(this.record,null,"\t"))}}renderQ1(employees){let content='\n          <div class="table-responsive">\n          <table class="table">\n            <thead>\n              <tr>\n                <th scope="col">Employee</th>\n                <th scope="col">Days to Birthday</th>\n                <th scope="col">Birthday</th>\n              </tr>\n            </thead>\n            <tbody>';employees.forEach(employee=>{content+=`\n              <tr>\n                <td>${employee.name}</td>\n                <td>${employee.daystobday}</td>\n                <td>${employee.birthdate}</td>\n              </tr>`}),content+="</tbody>\n          </table>\n        </div>",$(`#body-${this.question}`).html(content)}renderQ3(employees,p2=!1){let content='\n      <div class="table-responsive">\n      <table class="table">\n        <thead>\n          <tr>\n            <th scope="col">Employee</th>\n            <th scope="col">2013 Revenue</th>\n          </tr>\n        </thead>\n        <tbody>';for(const Emp of employees)(""!==Emp.supervisor||p2)&&(""!==Emp.supervisor&&p2||(content+=`\n          <tr>\n            <td>${Emp.name}</td>\n            <td>${Emp["2013 Revenue"]}</td>\n          </tr>`));content+="</tbody>\n      </table>\n    </div>",$(`#body-${this.question}`).html(content)}renderQ4(employees){let content='\n        <div class="table-responsive">\n        <table class="table">\n          <thead>\n            <tr>\n            <th scope="col">Employee</th>\n            <th scope="col">2013 Commission</th>\n            </tr>\n          </thead>\n          <tbody>';employees.forEach(employee=>{content+=`\n            <tr>\n              <td>${employee.name}</td>\n              <td>${employee.commission}</td>\n            </tr>`}),content+="</tbody>\n        </table>\n      </div>",$(`#body-${this.question}`).html(content)}renderQ5(employees){let content='\n          <div class="table-responsive">\n          <table class="table">\n            <thead>\n              <tr>\n              <th scope="col">Employee</th>\n              <th scope="col">Last Sale Date</th>\n              <th scope="col">Days Without a Sale</th>\n              </tr>\n            </thead>\n            <tbody>';employees.forEach(employee=>{content+=`\n              <tr>\n              <td>${employee.name}</td>\n                <td>${employee.lastsaledate}</td>\n                <td>${employee.dayssincesale}</td>\n              </tr>`}),content+="</tbody>\n          </table>\n        </div>",$(`#body-${this.question}`).html(content)}renderPerformanceQ(employees,worst=!1){let content='\n            <div class="table-responsive">\n            <table class="table">\n              <thead>\n                <tr>\n                  <th scope="col">Employee</th>\n                  <th scope="col">Customer</th>\n                  <th scope="col">Customer Total for Year</th>\n                </tr>\n              </thead>\n              <tbody>';for(const Emp of employees)!worst&&Emp.bestcustomer.totalsales<=0||worst&&Emp.worstperforming.totalsales<=0||(content+=`\n              <tr>\n                <td>${Emp.name}</td>\n                <td>${worst?Emp.worstperforming.name:Emp.bestcustomer.name}</td>\n                <td>${worst?Emp.worstperforming.totalsales:Emp.bestcustomer.totalsales}</td>\n              </tr>`);content+="</tbody>\n            </table>\n          </div>",$(`#body-${this.question}`).html(content)}}